INSERT INTO `tbl_textos` (`id`, `titulo`, `descricao`, `texto`, `foto`, `meta_title`, `meta_keywords`, `meta_description`, `pagina_referencia`) VALUES
(1, 'Sobre Nós', NULL, '<p>CEO Editor Ronaldo Medeiros</p>\r\n\r\n<p>No Jornal Brasil, acreditamos que a informação é uma das bases mais importantes para o crescimento pessoal e para a construção de uma sociedade justa e informada. Nossa missão é proporcionar aos nossos leitores um acesso fácil e rápido a notícias precisas, análises abrangentes e perspectivas equilibradas.</p>\r\n', '1694542186.7101-foto-N.png', NULL, NULL, NULL, 'Sobre'),
(4, 'Quem Somos Rodapé', NULL, '<p><span style=\"font-size:14px\"><span style=\"font-family:Open Sans,sans-serif\">redacao@jornalbrasil.com.br</span></span></p>\r\n\r\n<p>CEO Editor Ronaldo Medeiros</p>\r\n', '1607956555.72-foto-N.png', NULL, NULL, NULL, 'Sobre Rodapé'),
(21, 'Politica de Privacidade', NULL, '<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"color:#000000\"><span style=\"font-size:14px\">Ao acessar o JORNAL BRASIL, registramos informações como as páginas visitadas, seu navegador, sistema operacional e IP. Esses dados podem ser armazenados com segurança e criptografia.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"color:#000000\"><span style=\"font-size:14px\">Usamos cookies (pequenos arquivos salvos pelo navegador) para melhorar sua experiência, personalizando conteúdo e publicidade baseados em seus interesses. Se tiver dúvidas sobre nossa política de privacidade, contate-nos em redacao@jornalbrasil.com.br.</span></span></span></p>\r\n', '1694543237.3088-foto-N.png', NULL, NULL, NULL, NULL);

